b:if.script:
//
  (function() {
    const wrapper = document.getElementById('post-wrapper');
    const loader = document.getElementById('ias-loader');
    const noMore = document.getElementById('ias-nomore');
    let nextLink = document.getElementById('ias-next-link');
    let loading = false;

    if (!nextLink) return;

    const loadPosts = async () => {
      if (loading || !nextLink) return;
      loading = true;
      loader.style.display = 'block';

      try {
        const res = await fetch(nextLink.href);
        const html = await res.text();
        const doc = new DOMParser().parseFromString(html, 'text/html');
        const newPosts = doc.querySelectorAll('.post-card');

        if (newPosts.length > 0) {
          newPosts.forEach(p => wrapper.appendChild(p));
          const newNext = doc.getElementById('ias-next-link');
          if (newNext) { nextLink.href = newNext.href; } 
          else { nextLink = null; noMore.style.display = 'block'; }
        } else {
          nextLink = null;
          noMore.style.display = 'block';
        }
      } catch (e) { console.error(e); } 
      finally { loading = false; loader.style.display = 'none'; }
    };

    const obs = new IntersectionObserver((es) => { if (es[0].isIntersecting) loadPosts(); }, { rootMargin: '300px' });
    obs.observe(document.getElementById('ias-status'));
  })();
//
